package com.thoughtworks;import java.util.Arrays;import java.util.HashMap;import java.util.Map;import java.util.stream.Collectors;import static java.lang.String.valueOf;class FizzBuzzUtil {  private static Map<Integer, String> specificNumberAndSloganMap;  static {    specificNumberAndSloganMap = new HashMap<>();    specificNumberAndSloganMap.put(3, "Fizz");    specificNumberAndSloganMap.put(5, "Buzz");    specificNumberAndSloganMap.put(7, "Whizz");  }  static String fizzBuzz(int number) {    String correctSlogan = "";    if (isContainsCharacter(number, "7")) {      if (isContainsCharacter(number, "3")) {        return specificNumberAndSloganMap.get(3);      }      correctSlogan += getSloganByMultipleOfSpecificNumbers(number, new int[]{3, 7});      return correctSlogan;    } else if (isContainsCharacter(number, "5")) {      correctSlogan += getSloganByMultipleOfSpecificNumbers(number, new int[]{5, 7});      return correctSlogan;    } else if (isContainsCharacter(number, "3")) {      return "Fizz";    }    correctSlogan += getSloganByMultipleOfSpecificNumbers(number, new int[]{3, 5, 7});    if (correctSlogan.equals("")) correctSlogan += number;    return correctSlogan;  }  private static boolean isContainsCharacter(int number, String s) {    return valueOf(number).contains(s);  }  private static String getSloganByMultipleOfSpecificNumbers(int number, int[] specificNumbers) {    return Arrays.stream(specificNumbers)            .boxed()            .map(specificNumber -> number % specificNumber == 0 ? specificNumberAndSloganMap.get(specificNumber) : "")            .collect(Collectors.joining(""));  }}