package com.thoughtworks;import java.util.HashMap;import java.util.Map;import static java.lang.String.valueOf;class FizzBuzzUtil {  private static Map<Integer,String> specificNumberAndSloganMap;  static {    specificNumberAndSloganMap = new HashMap<>();    specificNumberAndSloganMap.put(3, "Fizz");    specificNumberAndSloganMap.put(5, "Buzz");    specificNumberAndSloganMap.put(7, "Whizz");  }  static String fizzBuzz(int number) {    String correctSlogan = "";    if (isContainsCharacter(number, "7")) {      if (isContainsCharacter(number, "3")) {        return specificNumberAndSloganMap.get(3);      }      if (number % 3 == 0) correctSlogan += specificNumberAndSloganMap.get(3);      if (number % 7 == 0) correctSlogan += specificNumberAndSloganMap.get(7);      return correctSlogan;    } else if (isContainsCharacter(number, "5")) {      correctSlogan += specificNumberAndSloganMap.get(5);      if (number % 7 == 0) correctSlogan += specificNumberAndSloganMap.get(7);      return correctSlogan;    } else if (isContainsCharacter(number, "3")) {      return "Fizz";    }    if (number % 3 == 0) correctSlogan += specificNumberAndSloganMap.get(3);    if (number % 5 == 0) correctSlogan += specificNumberAndSloganMap.get(5);    if (number % 7 == 0) correctSlogan += specificNumberAndSloganMap.get(7);    if (correctSlogan.equals("")) correctSlogan += number;    return correctSlogan;  }  private static boolean isContainsCharacter(int number, String s) {    return valueOf(number).contains(s);  }}